mapper.update:
UPDATE boardwrite
SET
    title = #{title}
  , description = #{description}
  , img = #{img}
  , writedate = #{writedate}
  , profileimage = #{profileimage}
WHERE
    boardid = #{boardid}

UPDATE groupwrite
SET
    region = #{region}
  , title = #{title}
  , line = #{line}
  , description = #{description}
  , peoplenum = #{peoplenum}
  , minage = #{minage}
  , titleimage = #{titleimage}
  , maxage = #{maxage}
  , writedate = #{writedate}
  , peoplenownum = #{peoplenownum}
  , once = #{once}
WHERE
    groupid = #{groupid}

UPDATE individualwrite
SET
    region = #{region}
  , meetdate = #{meetdate}
  , title = #{title}
  , description = #{description}
  , writedate = #{writedate}
  , maxage = #{maxage}
  , minage = #{minage}
WHERE
    individualid = #{individualid}

UPDATE usedwrite
SET
    region = #{region}
  , title = #{title}
  , description = #{description}
  , usedimage1 = #{usedimage1}
  , price = #{price}
  , writedate = #{writedate}
  , usedimage2 = #{usedimage2}
  , usedimage3 = #{usedimage3}
  , usedimage4 = #{usedimage4}
  , usedimage5 = #{usedimage5}
WHERE
    usedid = #{usedid}

UPDATE user
SET
    sns = #{sns}
  , nickname = #{nickname}
  , profileimage = #{profileimage}
  , modifyDate = #{modifydate}
  , bestcategory = #{bestcategory}
  , shortinfo = #{shortinfo}
    , birth = #{birth}
    , youtube = #{youtube}

WHERE
    userid = #{userid}

mapper.delete:
DELETE
FROM
    liked
WHERE categoryid = #{categoryid}
AND userid = #{userid}
AND inherentid = #{inherentid}

DELETE
FROM
    individualliked
WHERE categoryid = #{categoryid}
  AND userid = #{userid}
  AND inherentid = #{inherentid}

DELETE
FROM
    usedliked
WHERE categoryid = #{categoryid}
  AND userid = #{userid}
  AND inherentid = #{inherentid}

